function debounce(fn, delay) {
  let timeoutId;
  return function(...args) {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }
    timeoutId = setTimeout(() => {
      fn(...args);
      timeoutId = null;
    }, delay);
  };
}
// Debounce function to limit execution rate
// Useful for event handlers like scroll or resize

function deepClone(obj) {
  if (obj === null || typeof obj !== 'object') {
    return obj;
  }
  if (Array.isArray(obj)) {
    return obj.map(deepClone);
  }
  return Object.fromEntries(
    Object.entries(obj).map(([k, v]) => [k, deepClone(v)])
  );
}
// Deep clones objects and arrays recursively

type Result<T, E> = { success: true; value: T } | { success: false; error: E };
function tryCatch<T>(fn: () => T): Result<T, Error> {
  try {
    return { success: true, value: fn() };
  } catch (e) {
    return { success: false, error: e as Error };
  }
}
// Type-safe try-catch wrapper (TypeScript)

function memoize<T extends (...args: unknown[]) => unknown>(fn: T) {
  const cache = new Map<string, ReturnType<T>>();
  return ((...args: Parameters<T>): ReturnType<T> => {
    const key = JSON.stringify(args);
    if (cache.has(key)) {
      return cache.get(key)!;
    }
    const result = fn(...args);
    cache.set(key, result);
    return result;
  }) as T;
}
// Memoization function with type safety

